"""
58同城房产频道(https://sh.58.com/hezu/35689140862413x.shtml)字体加密
"""
from base64 import b64decode
from io import BytesIO
from fontTools.ttLib import TTFont
import re


class FangChan58Font:
    """
    58房产频道（https://sh.58.com/hezu/35689140862413x.shtml）（明文空间：0-9数字，密文空间：unicode）
    Desc：
        与信信宝类似，但glyf table也变了。glyphname与真实值差1，即
        glyph00001： 0
        glyph00002： 1
        ...
        glyph00010: 9
    """
    GLYF_TABLE = {
        'glyph00001': '0',
        'glyph00002': '1',
        'glyph00003': '2',
        'glyph00004': '3',
        'glyph00005': '4',
        'glyph00006': '5',
        'glyph00007': '6',
        'glyph00008': '7',
        'glyph00009': '8',
        'glyph00010': '9'
    }

    def __init__(self, b64_font):
        font_bytes = b64decode(b64_font)
        font = TTFont(BytesIO(font_bytes))
        self.cmap = font['cmap'].getBestCmap()
        self.CIPHER_TEXT_SPACE = list(self.cmap.keys())
        font.close()

    def decrypt(self, s):
        """
        根据字体文件解密字符串
        :param s: string
        :return:
        """
        out = list(s)
        span_chars = [(m.span(), m.group())
                      for m in re.finditer('&#x[0-9a-f]{4};', s)]
        for span, char in reversed(span_chars):
            out[span[0]:span[1]] = self.decrypt_char(char)
        return ''.join(out)

    def decrypt_char(self, char):
        """
        明文空间只有数字
        :param char: html编码的字符（如&#x9f64;&#x9e3a;&#x9476;&#x9476;）
        :return:
        """
        import re
        if re.match('&#x[0-9a-f]{4};', char):
            code = int(char[-5:-1], 16)
            if code in self.CIPHER_TEXT_SPACE:
                glyph_name = self.cmap[code]
                return self.GLYF_TABLE[glyph_name]
        return char


def _test():
    b64_font = ''
    font = FangChan58Font(b64_font)
    print(font.decrypt('&#x9f92;&#x9e3a;&#x9fa4;&#x9fa4;</b> 元/月'))
    print(font.decrypt('&#x9476;室&#x9476;厅&#x9476;卫 '))


if __name__ == '__main__':
    _test()
